import React, { useState, useEffect } from 'react';
import FullCalendar from '@fullcalendar/react';
import dayGridPlugin from '@fullcalendar/daygrid';
import timeGridPlugin from '@fullcalendar/timegrid';
import resourceTimeGridPlugin from '@fullcalendar/resource-timegrid'; // Import resourceTimeGridPlugin
import axios from 'axios';

const App = () => {
  const [events, setEvents] = useState([]);
  const [resources, setResources] = useState([]);

  useEffect(() => {
    axios.get("/api/availability").then(response => {
      const formattedEvents = response.data.map(item => ({
        title: item.court.split("_").join(" "),
        start: `${item.date}T${String(item.start).padStart(2, '0')}:00:00`,
        end: `${item.date}T${String(item.end).padStart(2, '0')}:00:00`,
        resourceId: item.court, // Assign each event to a court (resource)
        backgroundColor: item.slots > 0 ? "green" : "red"
      }));

      // Group resources by date and court
      const groupedResources = {};
      response.data.forEach(item => {
        if (!groupedResources[item.date]) {
          groupedResources[item.date] = {
            id: item.date,
            title: item.date, // Parent resource is the date
            children: [] // Courts will be added as children
          };
        }
        groupedResources[item.date].children.push({
          id: `${item.court}`,
          title: item.court.split("_").join(" ") // Child resource is the court
        });
      });

      setResources(Object.values(groupedResources)); // Convert grouped resources to an array
      setEvents(formattedEvents);

    });
  }, []);

  return (
    <div>
      <h1>Tennis Court Availability</h1>
      <FullCalendar
        plugins={[dayGridPlugin, timeGridPlugin, resourceTimeGridPlugin]} // Add resourceTimeGridPlugin
        initialView="resourceTimeGridWeek"
        events={events}
        resources={resources} // Pass resources to the calendar
        eventTimeFormat={{
          hour: '2-digit',
          minute: '2-digit',
          meridiem: false, // Use 24-hour format
        }}
        eventDisplay="block"
      />
    </div>
  );
};

export default App;
