import React, { useState, useEffect } from 'react';
import FullCalendar from '@fullcalendar/react';
import dayGridPlugin from '@fullcalendar/daygrid';
import timeGridPlugin from '@fullcalendar/timegrid';
import resourceTimeGridPlugin from '@fullcalendar/resource-timegrid'; // Import resourceTimeGridPlugin
import axios from 'axios';

const App = () => {
  const [events, setEvents] = useState([]);
  const [resources, setResources] = useState([]);

  useEffect(() => {
    axios.get("/api/availability").then(response => {
      const formattedEvents = response.data.map(item => ({
        title: item.court.split("_").join(" "),
        start: `${item.date}T${String(item.start).padStart(2, '0')}:00:00`,
        end: `${item.date}T${String(item.end).padStart(2, '0')}:00:00`,
        resourceId: `${item.date}_${item.court}`, // Assign each event to a court under a specific date
        backgroundColor: item.slots > 0 ? "green" : "red"
      }));

      // Group resources by date with two subcolumns for courts
      const groupedResources = {};
      response.data.forEach(item => {
        if (!groupedResources[item.date]) {
          groupedResources[item.date] = {
            id: item.date,
            title: item.date, // Parent resource is the date
            children: [
              { id: `${item.date}_rosemary_garden_tennis`, title: "Rosemary Garden Tennis" },
              { id: `${item.date}_oak_park_tennis`, title: "Oak Park Tennis" }
            ] // Two subcolumns for courts
          };
        }
      });

      setResources(Object.values(groupedResources)); // Convert grouped resources to an array
      setEvents(formattedEvents);
    });
  }, []);

  return (
    <div>
      <h1>Tennis Court Availability</h1>
      <FullCalendar
        plugins={[dayGridPlugin, timeGridPlugin, resourceTimeGridPlugin]} // Use resourceTimeGridPlugin
        initialView="resourceTimeGridWeek" // Use resourceTimeGridWeek
        events={events}
        resources={resources} // Pass grouped resources to the calendar
        eventTimeFormat={{
          hour: '2-digit',
          minute: '2-digit',
          meridiem: false, // Use 24-hour format
        }}
        eventDisplay="block"
      />
    </div>
  );
};

export default App;
